INSERT INTO `%DB_PREFIX%article_copy` VALUES ('7','2014-5-9     p2p信贷即将上线','','<div><span style=\"font-size:14px;line-height:21px;\">尊敬的p2p信贷用户：</span></div>\r\n<div><span style=\"font-size:14px;line-height:21px;\"><br />\r\n</span></div>\r\n<div><span style=\"font-size:14px;line-height:21px;\">&nbsp; &nbsp; &nbsp; &nbsp; 您好！</span></div>\r\n<div><span style=\"font-size:14px;line-height:21px;\"><br />\r\n</span></div>\r\n<div style=\"text-align:left;\"><span style=\"font-size:14px;line-height:21px;\">&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"font-size:14px;line-height:21px;\">p2p信贷正在测试中，</span><span style=\"font-size:14px;line-height:21px;\">预计2013年3月正式上线。具体上线日期确定后我们会及时予以公布。</span></div>\r\n<div><span style=\"font-size:14px;line-height:21px;\"><br />\r\n</span></div>\r\n<div><span style=\"font-size:14px;line-height:21px;\">&nbsp; &nbsp; &nbsp; &nbsp; 非常感谢各位用户的支持和信任！我们会不断完善、为您提供更加优质的服务！</span></div>\r\n<div><span style=\"font-size:14px;line-height:21px;\"><br />\r\n</span></div>\r\n<div style=\"text-align:right;\"><span style=\"font-size:14px;line-height:21px;\">p2p信贷团队</span></div>\r\n<div style=\"text-align:right;\"><span style=\"font-size:14px;line-height:21px;\"><br />\r\n</span></div>\r\n<div style=\"text-align:right;\"><span style=\"font-size:14px;line-height:21px;\">2013年2月4日</span></div>','5','1356389066','1399569847','0','1','','0','0','652','23','','','','','','');
